import React, { useState } from "react";
import type { ChangeEvent, KeyboardEvent } from "react";
import axios from "axios";
import "./Chatbot.css";

interface Message {
  role: "user" | "assistant";
  content: string;
}

interface PatientProfile {
  [key: string]: string;
}

const Chatbot: React.FC = () => {
  const [userInput, setUserInput] = useState("");
  const [messages, setMessages] = useState<Message[]>([]);
  const [profile, setProfile] = useState("Anxious Patient");
  const userId = "user123"; // Ideally, this would be dynamic for real users

  const patientProfiles: PatientProfile = {
    "Angry Patient": "gets easily frustrated and feels misunderstood.",
    "Anxious Patient": "struggles with anxiety and overthinks situations.",
    "Depressed Patient": "feels hopeless and lacks motivation.",
    "Trauma Survivor": "has experienced trauma and is hesitant to open up."
  };

  const resetChat = async (): Promise<void> => {
    setMessages([]); // Clear messages on frontend

    try {
      await axios.post("http://127.0.0.1:5000/reset", { user_id: userId });
    } catch (error) {
      console.error("Error resetting chat:", error);
    }
  };

  const sendMessage = async (): Promise<void> => {
    if (!userInput) return;

    const newMessages: Message[] = [...messages, { role: "user", content: userInput }];
    setMessages(newMessages);
    setUserInput("");

    try {
      const response = await axios.post("http://127.0.0.1:5000/chat", {
        user_id: userId,
        message: userInput,
        profile: patientProfiles[profile]
      });

      setMessages([...newMessages, { role: "assistant", content: response.data.response }]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  const handleKeyPress = (e: KeyboardEvent<HTMLInputElement>): void => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  return (
    <div className="container">
      <h2 style={{ marginBottom: "20px" }}>AI Therapy Chat</h2>
      <select 
        style={{
          padding: "8px",
          marginBottom: "20px",
          borderRadius: "4px",
          border: "1px solid #ddd"
        }}
        onChange={(e: ChangeEvent<HTMLSelectElement>) => {
          setProfile(e.target.value);
          resetChat();
        }} 
        value={profile}
      >
        {Object.keys(patientProfiles).map((key) => (
          <option key={key} value={key}>{key}</option>
        ))}
      </select>
      <div style={{ 
        flex: 1,
        border: "1px solid #ddd",
        borderRadius: "4px",
        padding: "10px",
        marginBottom: "20px",
        overflowY: "auto",
        backgroundColor: "white"
      }}>
        {messages.map((msg, index) => (
          <div 
            key={index} 
            style={{ 
              textAlign: msg.role === "user" ? "right" : "left",
              marginBottom: "10px",
              padding: "8px",
              borderRadius: "4px",
              backgroundColor: msg.role === "user" ? "#e3f2fd" : "#f5f5f5"
            }}
          >
            <b>{msg.role === "user" ? "You" : "AI"}:</b> {msg.content}
          </div>
        ))}
      </div>
      <div style={{ display: "flex", gap: "10px" }}>
        <input
          type="text"
          value={userInput}
          onChange={(e: ChangeEvent<HTMLInputElement>) => setUserInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type a message..."
          style={{
            flex: 1,
            padding: "8px",
            borderRadius: "4px",
            border: "1px solid #ddd"
          }}
        />
        <button 
          onClick={sendMessage}
          style={{
            padding: "8px 16px",
            borderRadius: "4px",
            border: "none",
            backgroundColor: "#2196f3",
            color: "white",
            cursor: "pointer"
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default Chatbot;
