import React, { useState } from "react";
import axios from "axios";

const Chatbot = () => {
  const [userInput, setUserInput] = useState("");
  const [messages, setMessages] = useState([]);
  const [profile, setProfile] = useState("Anxious Patient");
  const userId = "user123"; // Ideally, this would be dynamic for real users

  const patientProfiles = {
    "Angry Patient": "gets easily frustrated and feels misunderstood.",
    "Anxious Patient": "struggles with anxiety and overthinks situations.",
    "Depressed Patient": "feels hopeless and lacks motivation.",
    "Trauma Survivor": "has experienced trauma and is hesitant to open up."
  };

  const sendMessage = async () => {
    if (!userInput) return;

    const resetChat = () => {
      setMessages([]); // Clear messages in the frontend
      axios.post("http://127.0.0.1:5000/reset", { user_id }); // Optional: Reset on backend
    };

    const newMessages = [...messages, { role: "user", content: userInput }];
    setMessages(newMessages);
    setUserInput("");

    try {
      const response = await axios.post("http://127.0.0.1:5000/chat", {
        user_id: userId,
        message: userInput,
        profile: patientProfiles[profile]
      });

      setMessages([...newMessages, { role: "assistant", content: response.data.response }]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div style={{ width: "400px", margin: "auto" }}>
      <h2>AI Therapy Chat</h2>
      <select onChange={(e) => {
        setProfile(e.target.value)
        resetChat()
        }} value={profile}>
        {Object.keys(patientProfiles).map((key) => (
          <option key={key} value={key}>{key}</option>
        ))}
      </select>
      <div style={{ border: "1px solid black", padding: "10px", height: "300px", overflowY: "scroll" }}>
        {messages.map((msg, index) => (
          <div key={index} style={{ textAlign: msg.role === "user" ? "right" : "left" }}>
            <b>{msg.role === "user" ? "You" : "AI"}:</b> {msg.content}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        onKeyPress={(e) => e.key === "Enter" && sendMessage()}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chatbot;
